from notebookutils import mssparkutils
from pyspark.sql.types import *
from pyspark.sql.functions import *

# input destination for streaming data
input_path = 'abfss://hallinc@hallincdatalake.dfs.core.windows.net/streaming_data/'

# Create a stream that reads data from the folder, using a JSON schema
jsonSchema = StructType([
StructField("device", StringType(), False),
StructField("status", StringType(), False)
])

# start the structured stream from input location with json schema
iotstream = spark.readStream \
    .schema(jsonSchema) \
    .option("maxFilesPerTrigger", 1) \
    .json(input_path)

# Write some event data to the folder
device_data = '''
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"ok"}
{"device":"Dev2","status":"error"}
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"error"}
{"device":"Dev2","status":"ok"}
{"device":"Dev2","status":"error"}
{"device":"Dev1","status":"ok"}
'''
# write data into text file with mssparkutils
mssparkutils.fs.put(input_path + "data.txt", device_data, True)
print("Source stream created...")

# Add more data to the source stream

more_data = '''
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"ok"}
{"device":"Dev1","status":"error"}
{"device":"Dev2","status":"error"}
{"device":"Dev1","status":"ok"}
'''

mssparkutils.fs.put(input_path + "more-data.txt", more_data, True)
